import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import { Link } from "react-router-dom";
import { BrowserRoute, Router, Route } from "react-router-dom";
// import "../js/jquery-3.3.1.slim.min.js";
// import "../js/bootstrap.min.js";

class MenuHeader extends React.Component {
  constructor(props) {
    super(props);
    this.state = { category: "" };
  }

  sepakBola = () => {
    this.setState({ category: "sepak bola" }, () => {
      this.props.onClick(this.state.category);
      console.log(this.state.category);
    });
  };

  ekonomi = () => {
    this.setState({ category: "ekonomi" }, () => {
      this.props.onClick(this.state.category);
      console.log(this.state.category);
    });
  };

  politik = () => {
    this.setState({ category: "politik" }, () => {
      this.props.onClick(this.state.category);
      console.log(this.state.category);
    });
  };

  hiburan = () => {
    this.setState({ category: "hiburan" }, () => {
      this.props.onClick(this.state.category);
      console.log(this.state.category);
    });
  };

  render() {
    return (
      <BR>
        <div className="menu-header">
          <table className="table-center">
            <tbody>
              <tr className="align-center">
                <td className="menu-item-list">
                  <Link to="/sepak_bola">SepakBola</Link>
                </td>
                <td className="menu-item-list">
                  <Link to="/ekonomi">Ekonomi</Link>
                </td>
                <td className="menu-item-list">
                  <Link to="/politik">Politik</Link>
                </td>
                <td className="menu-item-list">
                  <Link to="/hiburan">Hiburan</Link>
                </td>
                <td className="menu-item-list">
                  <div className="dropdown">
                    <button
                      className="btn dropdown-toggle lainnya"
                      type="button"
                      id="dropdownMenuButton"
                      data-toggle="dropdown"
                      aria-haspopup="true"
                      aria-expanded="false"
                    >
                      lainnya
                    </button>
                    <div
                      className="dropdown-menu"
                      aria-labelledby="dropdownMenuButton"
                    >
                      <a className="dropdown-item" href="#">
                        Action
                      </a>
                      <a className="dropdown-item" href="#">
                        Another action
                      </a>
                      <a className="dropdown-item" href="#">
                        Something else here
                      </a>
                      <a className="dropdown-item" href="#">
                        tes
                      </a>
                    </div>
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
          {/* <Route path="/:id" component={Child} />
          <Route
            path="/order/:direction(asc|desc)"
            component={ComponentWithRegex}
          /> */}
        </div>
      </BR>
    );
  }
}

