import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import Header from "../components/header";
import Content from "../components/content";
const axios = require("axios");

class Page extends React.Component {
  constructor(props) {
    super(props);
    this.host =
      "https://newsapi.org/v2/top-headlines?" +
      "country=us&" +
      "apiKey=b827b56e281a49d6960c90e09099efc8";
    this.state = { news: [], title: [] };
  }

  componentDidMount() {
    axios.get(this.host).then(response => {
      this.setState({ news: response.data.articles.description });
      this.setState({ title: response.data.articles.title });
      console.log(response.data.articles[0].author);
      // console.log(this.state.news[0].description);
    });
  }

  render() {
    // console.log(this.state.news);
    return (
      <div>
        {/* <h1>{this.state.news}</h1> */}
        <Header />
        <div class="blank" />
        <Content content={this.state.news} title={this.state.title} />
        <div class="blank" />
      </div>
    );
  }
}

export default Page;
