import createStore from "unistore";

export const store = createStore({
  username: "abc",
  email: "",
  status: "",
  searchInput: ""
});

export const actions = () => ({
  setNama(state, name) {
    return { username: name };
  },
  setEmail(state, em) {
    return { email: em };
  },
  setStatus(state, stat) {
    return { status: stat };
  }
});
