import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import { Link, Redirect, withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "./store";

// import "../js/jquery-3.3.1.slim.min.js";
// import "../js/bootstrap.min.js";

class MenuHeader extends React.Component {
  constructor(props) {
    super(props);
    this.state = { category: "", status: "" };
  }

  componentDidMount() {
    this.setState({ status: localStorage.getItem("status") });
  }

  sepakBola = () => {
    this.props.setSearchInput("premier leaguee");
  };

  ekonomi = () => {
    this.props.setSearchInput("economyy");
  };

  politik = () => {
    this.props.setSearchInput("politicss");
  };

  hiburan = () => {
    this.props.setSearchInput("entertainmentss");
  };

  signup = () => {
    this.props.setStatus("false");
    // this.setState({ status: "false" }, () => {
    // });
  };

  renderRedirect = () => {
    if (this.props.status === "false") {
      return <Redirect to="/" />;
    }
  };

  render() {
    // console.log(this.state.status);
    return (
      <div className="menu-header">
        {this.renderRedirect()}
        <table className="table-center">
          <tbody>
            <tr className="align-center">
              <td className="menu-item-list">
                <Link to="/sepak_bola" onClick={this.sepakBola}>
                  SepakBola
                </Link>
              </td>
              <td className="menu-item-list">
                <Link to="/ekonomi" onClick={this.ekonomi}>
                  Ekonomi
                </Link>
              </td>
              <td className="menu-item-list">
                <Link to="/politik" onClick={this.politik}>
                  Politik
                </Link>
              </td>
              <td className="menu-item-list">
                <Link to="/hiburan" onClick={this.hiburan}>
                  Hiburan
                </Link>
              </td>
              <td className="menu-item-list" onClick={this.signup}>
                Signup
              </td>
              <td className="menu-item-list">
                <Link to="/profil">Profil</Link>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default connect(
  "status",
  actions
)(withRouter(MenuHeader));
