import createStore from "unistore";

export const store = createStore({
  username: "abc",
  email: "",
  status: ""
});

export const actions = () => ({
  setNama(state, name) {
    return { username: name };
  },
  setEmail(state, em) {
    return { email: em };
  },
  toggleStatus(state) {
    return { status: !state.status };
  }
});
