import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import { connect } from "unistore/react";
import { actions } from "../components/store";
const axios = require("axios");

class SearchHeader extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount = () => {
    let hostNews = "https://newsapi.org/v2/everything?";
    let query = "q=Indonesia&";
    let apikey = "apikey=1bc92a837b9f46b6a433a86582beab5b";
    let url = hostNews + query + apikey;
    if (this.props.searchInput == "") {
      axios.get(url).then(response => {
        this.props.setArticle(response.data.articles);
      });
    } else {
      url = hostNews + "q=" + this.props.searchInput + "&" + apikey;
      axios.get(url).then(response => {
        this.props.setArticle(response.data.articles);
      });
    }
  };

  onChange = e => {
    const input = e.target.value;

    this.props.setSearchInput(input);
    let hostNews = "https://newsapi.org/v2/everything?";
    let query = "q=Indonesia&";
    let apikey = "apikey=1bc92a837b9f46b6a433a86582beab5b";
    let url = hostNews + query + apikey;
    if (this.props.searchInput == "") {
      console.log(url);
      axios.get(url).then(response => {
        this.props.setArticle(response.data.articles);
      });
    } else {
      console.log(url);
      url = hostNews + "q=" + this.props.searchInput + "&" + apikey;
      axios.get(url).then(response => {
        this.props.setArticle(response.data.articles);
      });
    }
  };

  render() {
    return (
      <table className="table-center search">
        <tbody>
          <tr>
            <td>
              <input
                type="search"
                placeholder="search"
                onChange={this.onChange}
              />
            </td>
          </tr>
        </tbody>
      </table>
    );
  }
}

export default connect(
  "searchInput",
  actions
)(SearchHeader);
