import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import BeritaTerkiniBar from "./berita-terkini-bar";
import BeritaTerkini from "./berita-terkini";
import Headline from "./headline";
import { connect } from "unistore/react";
import { actions } from "../components/store";

class Content extends React.Component {
  componentDidMount = () => {
    hostNews = "https://newsapi.org/v2/everything?";
    query = "q=Indonesia&";
    apikey = "apikey=1bc92a837b9f46b6a433a86582beab5b";
    let url = hostNews + query + apikey;
    if (this.props.searchInput == "") {
      axios.get(url).then(response => {
        this.setArticle(response.data.articles);
      });
    } else {
      url = this.hostNews + "q=" + this.props.searchInput + "&" + apikey;
      axios.get(url).then(response => {
        this.setArticle(response.data.articles);
      });
    }
  };
  render() {
    // console.log(this.props.content[0]);
    let headlineNews = [];
    let a = [];
    if (this.props.content.length > 0) {
      headlineNews = this.props.content[0];
      a = this.props.content.slice(1, 4);
    }

    return (
      <div className="container">
        <div className="row">
          <div className="col-4">
            <BeritaTerkiniBar />
            {a.map(content => {
              return (
                <BeritaTerkini
                  content={content.content}
                  title={content.title}
                />
              );
            })}
          </div>
          <div className="col-8">
            <Headline headlineNews={headlineNews} />
          </div>
        </div>
      </div>
    );
  }
}

export default connect(
  "",
  actions
)(Content);
