import React from "react";
import "../css/style.css";
import "../css/bootstrap.min.css";
import Header from "../components/header";
import Content from "../components/content";
const axios = require("axios");

class Page extends React.Component {
  constructor(props) {
    super(props);
    this.hostNews =
      "https://newsapi.org/v2/everything?" +
      "apiKey=b827b56e281a49d6960c90e09099efc8";
    // "country=us&" +
    this.state = {
      news: [{ description: "description", title: "title" }],
      searchInput: ""
    };
  }

  componentWillMount() {
    if (this.setState.searchInput == "") {
      axios.get(this.hostNews).then(response => {
        this.setState({ news: response.data.articles });
      });
    } else {
      // "country=us&" +
      this.hostNews =
        "https://newsapi.org/v2/everything?" +
        "q=indonesia" +
        this.state.searchInput +
        "&" +
        "apiKey=b827b56e281a49d6960c90e09099efc8";
      axios.get(this.hostNews).then(response => {
        this.setState({ news: response.data.articles });
      });
    }
  }

  onChange = keyword => {
    this.setState({ searchInput: keyword });
    // "country=us&" +
    this.hostNews =
      "https://newsapi.org/v2/everything?" +
      "q=" +
      this.state.searchInput +
      "&" +
      "apiKey=b827b56e281a49d6960c90e09099efc8";
    axios.get(this.hostNews).then(response => {
      this.setState({ news: response.data.articles });
    });
  };

  render() {
    // console.log(this.state.news);
    console.log(this.state.news);
    console.log(this.state.searchInput);
    return (
      <div>
        <Header onChange={this.onChange} />
        <div className="blank" />
        <Content content={this.state.news} />
        <div className="blank" />
      </div>
    );
  }
}

export default Page;
